// Get the incoming item which contains our data
const item = $input.first().json;

// Get the array from the 'categoriesJson' field
const categories = item.categoriesArray;

// Format the array into a clean text block for the LLM prompt
const promptCategories = categories.map(cat => {
  return `- ${cat.id}: ${cat.description}`;
}).join('\n');

// Add the new prompt string as a field to the item
item.promptCategories = promptCategories;

// Return the modified item
return item;

